import React, {useState} from "react";
import "./loginform.css"

function LoginForm(){
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    
    async function loginData(){
        var myHeaders = new Headers();
myHeaders.append("Accept", "application/json");

var formdata = new FormData();
formdata.append("email", email);
formdata.append("password", password);

var requestOptions = {
  method: 'POST',
  headers: myHeaders,
  body: formdata,
  redirect: 'follow'
};

fetch("https://staging.hospyta.com/auth/login", requestOptions)
  .then(response => response.text())
  .then(result => console.log(result.status))
  .catch(error => console.log('error', error));
        
    }

    return (
        <div className="cover">
            <h1>Login</h1>
            <input type="text" placeholder="username" onChange={(e) => setEmail(e.target.value)} className="form-control" />
            <input type="password" placeholder="password"  onChange={(e) => setPassword(e.target.value)} className="form-control" />

            <div className="login-btn" onClick={loginData}>Login</div>

        </div>
    )
}

export default LoginForm
