import React, {useState, useEffect} from 'react'
import '../index.css'
import axios from 'axios'
const Register = () => {

  const [value,setValue]=useState({
    username:"",
    email:"",
    password:"",
    confirmPassword:""
  })
  const [data, setdata] = useState('');
  let toastOption={
    position:"bottom-right",
    autoClose:8000,
    pauseOnHover:true,
    draggable:true,
    theme:"dark"
  }

    let handleSubmit= (e)=>{
      let {username, email, password}=value
      e.preventDefault()
      handleValidation()
      axios.post('http://localhost:4000/post',value)
      toast.error("Data Submitted Succesfully", toastOption)
      
    }
    let handleChange=(e)=>{
      setValue({...value, [e.target.name]:e.target.value})
    }
   let handleValidation=()=>{
        let {email,username, password, confirmPassword}=value;
        if(password!==confirmPassword)
        {
          toast.error("Password and confirm password must be same",toastOption)
          return false
        }
        else if(username.length <3){
          toast.error("Username must have atleast three characters",toastOption)
          return false
        }
        else if(email==="" ){
          toast.error("Email is required",toastOption)
          return false
        }
        else if(password.length<8){
          toast.error("Password must have atleast eight character ",toastOption)
        }
        return false
      }
      const [user,setUser]=useState([])
    // axios.get("http://localhost:4000/get")
   

  return (
    <>
     
      
      <div className='FormContainer'>

        { console.log(data) }
          <form onSubmit={(e)=>{handleSubmit(e)}}>
                 <input type="email" placeholder='Email' name='email' onChange={(e)=>handleChange(e)}/>
                 <input type="password" placeholder='Password' name='password' onChange={(e)=>handleChange(e)}/>
                  <button className='button' type='submit' >CREATE USER</button>
          </form>
          </div>
 
  
    </>
  )
}

export default Register
