import React, {useState} from "react";
import "./loginform.css"

function LoginForm(){
    const [username, setEmail] = useState("");
    const [userpassword, setPassword] = useState("");
    
    async function loginData(){
        console.warn(email, password);
        let item = {email, password};
        let result = await fetch("https://staging.hospyta.com/auth/login", {
            method: 'POST',
            headers:{
                "Accept": "application/json"
            },
            body: JSON.stringify(email=>username, userpassword)
        });
        result = await result.json();
        console.log(result);
        
    }

    return (
        <div className="cover">
            <h1>Login</h1>
            <input type="text" placeholder="username" onChange={(e) => setEmail(e.target.value)} className="form-control" />
            <input type="password" placeholder="password"  onChange={(e) => setPassword(e.target.value)} className="form-control" />

            <div className="login-btn" onClick={loginData}>Login</div>

        </div>
    )
}

export default LoginForm
