import React, {useEffect, useState} from "react";
import "./loginform.css"

function LoginForm(){
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    
    async function loginData(){
        let item = {email, password};
        console.log(item);
        let result = await fetch("https://staging.hospyta.com/auth/login", {
            method: "post",
            body: JSON.stringify(item),
            headers:{'Accept': 'application/json'},
        });
        result = await result.json();
        console.log(result);
        
    }

    return (
        <div className="cover">
            <h1>Login</h1>
            <input type="email" placeholder="email" onChange={(e) => setEmail(e.target.value)} className="form-control" />
            <input type="password" placeholder="password"  onChange={(e) => setPassword(e.target.value)} className="form-control" />

            <button Onclick={()=>logindata()} className="btn btn-paimary">Login</button>

        </div>
    )
}

export default LoginForm
