import React, {useEffect, useState} from "react";
// import {useHistory} from "react-router-dom";
// import { gapi } from "gapi-script";
import "./loginform.css"
function LoginForm(){
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    // const history = useHistory();
    useEffect(() => {
        if(localStorage.getItem("user-info")){

        }

    }, [])
    async function login(){
        console.warn(email, password)
        let item = {email, password};
        let result = await fetch("https://staging.hospyta.com/auth/login", {
            method: 'POST',
            headers:{
                "Accept": "application/json"
            },
            body: JSON.stringify(item)
        });
        result = await result.json();
        localStorage.setItem(JSON.stringify(result))
    }

    return (
        <div className="cover">
            <h1>Login</h1>
            <input type="text" placeholder="username" onClick={(e) => setEmail(e.target.value)} className="form-control" />
            <input type="password" placeholder="password"  onClick={(e) => setPassword(e.target.value)} className="form-control" />

            <div className="login-btn" onClick={login}>Login</div>

        </div>
    )
}

export default LoginForm
