import React, {useEffect, useState} from "react";
import "./loginform.css"

function LoginForm(){

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    
    async function loginData(){
        var data = new FormData();
data.append("email", "superadmin@gmail.com");
data.append("password", "password");

var xhr = new XMLHttpRequest();
xhr.withCredentials = true;

xhr.addEventListener("readystatechange", function() {
  if(this.readyState === 4) {
    console.log(this.responseText);
  }
});

xhr.open("POST", "https://staging.hospyta.com/auth/login");
xhr.setRequestHeader("Accept", "application/json");

xhr.send(data);
        
    }

    return (
        <div className="cover">
            <h1>Login</h1>
            <input type="email" placeholder="email" onChange={(e) => setEmail(e.target.value)} className="form-control" />
            <input type="password" placeholder="password"  onChange={(e) => setPassword(e.target.value)} className="form-control" />

            <button onClick={()=>loginData()} className="btn btn-paimary">Login</button>

        </div>
    )
}

export default LoginForm
